import osdef start():    print()    maplist = []    if 'maps_here' not in os.listdir():        os.mkdir('maps_here')        print("'maps_here' folder created, place your maps inside of 'maps_here'\n")        return input('[OK] ')    for i in os.listdir('maps_here'):        try:            maplist.append(i.rsplit('.png', 1)[0])        except:            continue    if maplist == []:        print("'maps_here' is empty, place your maps inside of 'maps_here'\n")        return input('[OK] ')    value = 1    while 'sampleMapRotation{}.txt'.format(str(value)) in os.listdir():        value += 1    with open('sampleMapRotation{}.txt'.format(str(value)), 'w+') as file:        file.write('\n'.join(maplist))    print("created '{}'\n".format('sampleMapRotation{}.txt'.format(str(value))))    return input('[OK] ')if __name__ == '__main__':    start()